	var hasTracySecurityAdminRole = gs.hasRole("x_trz.tracy_security_admin");

	console.log(hasTracySecurityAdminRole);

	if(hasTracySecurityAdminRole || isInstanceInProduction()) {
		answer = true;
		return;
	}

	answer = false;

})();


function isInstanceInProduction(){
	var currentInstance = gs.getProperty('instance_name');
	var prodInstance = gs.getProperty('x_trz.prod_instance');
	if(currentInstance === prodInstance) {
				return true;
	}
	return false;
}
