sp_widget.client_script:
function($scope, $compile, $sce) {
  /* widget controller */
  var c = this;

	$scope.template = $sce.trustAsHtml(c.data.template);



}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
   function getXMLString(sys_id){
	  var gr = new GlideRecord("x_trz_version");
	  gr.get(sys_id);
	  return gr.getValue("payload");
  }



  var xmlString1 = getXMLString("061164a7dbe0e3400414d141ce961945");

	var xmlString2 = getXMLString("8210646bdbe0e3400414d141ce96193d");

//  console.log(xmlString1);
//  console.log(xmlString2);



  var diffHelper = new global.TracyDiffHelperGlobal();
  var diff = diffHelper.diffXMLString(xmlString1, xmlString2);
	var left = "Previous Version";
	var right = "Update Set Version";
	var template = diffHelper.getTemplate(diff, left, right);

	/*template = template
		.replace('<?xml version="1.0" encoding="UTF-8"?>', "")
		.replace('<tbody>', "")
		.replace('</tbody>', "");*/

  console.log(diff);
  console.log(template);

	data.template = template



	if(xmlString1 !== xmlString2){
		console.log("xmls not equal")
	}else {
		console.log("xmls equal");
	}




})();
