var HttpService = Class.create();
HttpService.prototype = {
  initialize: function() {
    this.masterInstanceUrl = gs.getProperty("");
    this.basicAuthUser = gs.getProperty("");
    this.basicAuthPassword = gs.getProperty("");
  },

  createNewChangeVersion: function(changedRecord) {
    try {
      var request = new sn_ws.RESTMessageV2();
      request.setEndpoint(this.masterInstanceUrl);
      request.setHttpMethod("POST");

      request.setBasicAuth(this.basicAuthUser, this.basicAuthPassword);
      request.setRequestHeader("Accept", "application/json");
      request.setRequestHeader("Content-Type", "application/json");
      request.setRequestBody(JSON.stringify(newRecord));
      var response = request.execute();
      var body = request.getBody();
      var statusCode = JSON.parse(request.getStatusCode());

      return {
        body: body,
        statusCode: statusCode
      };

    } catch (exception) {
      gs.error(exception.message);  
    }


  },

  type: "HttpService"
};
