sys_script_include.script:
var HttpService = Class.create();
HttpService.prototype = {
  initialize: function() {
  	this.masterInstanceUrl = gs.getProperty('x_trz.master_instance_url');
    this.basicAuthUser = gs.getProperty("x_trz.username");
    this.basicAuthPassword = gs.getProperty("x_trz.password");
    this.updateTableUrl = "/api/now/table/x_trz_version";
  },

  createNewChangeVersion: function(record) {
    try {
    //  gs.info('trying to create new change version');

      var request = new sn_ws.RESTMessageV2();
      request.setEndpoint(this.masterInstanceUrl + this.updateTableUrl);
      request.setHttpMethod("POST");

      request.setBasicAuth(this.basicAuthUser, this.basicAuthPassword);
      request.setRequestHeader("Accept", "application/json");
      request.setRequestHeader("Content-Type", "application/json");
      request.setRequestBody(JSON.stringify(record));

	 // gs.info("HttpService.createNewChangeVersion => requestbody => record.json => " + JSON.stringify(record));

      var response = request.execute();
      var body = response.getBody();
      var statusCode = JSON.parse(response.getStatusCode());

      return {
        body: body,
        statusCode: statusCode
      };

    } catch (exception) {
      gs.info("Error!!" + exception.getMessage());  
      gs.error(exception.getMessage());  
    }


  },



  type: "HttpService"
};
