sys_script_include.script:
var UpdateVersionUtil = Class.create();
UpdateVersionUtil.prototype = {
	initialize: function() {
		//this.updateVersionTable = "sys_update_version";
	},

	findLatestVersion: function(updatedRecord) {

		gs.info('Tracy inside Findlsatversion');
		gs.info('updated record sysid ' + updatedRecord.getUniqueValue());
		var sysId = updatedRecord.sys_id.toString();
		gs.info("updated record sys id: " + sysId);
		var gr = new GlideRecord("sys_update_version");
		gr.addQuery("name", "LIKE", sysId);
		gr.addQuery("state", "=","current");
		gr.setLimit(1);
		gr.query();
		gr.next();
		return gr;
	},

	getVersionData: function(updateVersionRecord){
		//var gr = updateVersionRecord;
		var versionData = {
			name: updateVersionRecord.getValue("record_name"),
			payload: updateVersionRecord.getValue("payload"),
			instance_name: updateVersionRecord.getValue("instance_name"),
			action: updateVersionRecord.getValue("action"), 
			update_set: updateVersionRecord.getValue("source"),
			type: updateVersionRecord.getValue("type"),
		};
		return versionData;
	},

	type: 'UpdateVersionUtil'
};
