sys_script_include.script:
var UpdateVersionUtil = Class.create();
UpdateVersionUtil.prototype = {
    initialize: function() {
      //this.updateVersionTable = "sys_update_version";
    },

    findLatestVersion: function(updatedRecord) {
      var sysId = updatedRecord.sys_id.toString();
      var gr = new GlideRecord("sys_update_version");
      gr.addQuery("name", "LIKE", sysId);
      gr.addQuery("state", "=","current");
      gr.query();
      gr.next();
      return gr;
    },

    getVersionData: function(updateVersionRecord){
      var gr = updateVersionRecord;
      var versionData = {
        name: gr.record_name.toString(),
        payload: gr.payload.toString(),
        instance_name: gr.instance_name.toString(),
        action: gr.action.toString(), 
        update_set: gr.source.toString(),
        type: gr.type.toString(),
      };
      return versionData;
    },

    type: 'UpdateVersionUtil'
};
