sys_script_include.script:
var VersionGlideAjax = Class.create();
VersionGlideAjax.prototype = Object.extendsObject( global.AbstractAjaxProcessor, {
    type: "VersionGlideAjax",

    getRecordEnvironmentsWithAnyVersionsAsync: function() {
      var recordId = this.getParameter("sysparm_record_id");
      var _environments = [];

      if (!recordId)
        return JSON.stringify({
          error: "Required sysparm_record_id.",
          environments: null
        });

      var gr = new GlideRecord("x_trz_version");
      gr.addQuery("remote_script_sys_id", recordId);
      gr.addQuery("state", "current");
      gr.addEncodedQuery("environment=Production^ORenvironment=Development^ORenvironment=Test");
      gr.query();

      while (gr.next()) {
        var versionEnvironment = gr.getValue("environment");
        if (versionEnvironment) {
            _environments.push(versionEnvironment);
        }
      }

      return JSON.stringify({
        error: null,
        environments: _environments
      });
    }
});
