sys_script_include.script:
var TracyService = Class.create();
TracyService.prototype = {
  initialize: function() {},

  updateVersion: function(record) {
  //  gs.info("TracyService: Starting... - SCRIPT INCLUDE TracyService");

    var remote_script_sys_id = record.getValue("sys_id");
gs.info("TracyService: Starting... - SCRIPT INCLUDE TracyService"+remote_script_sys_id);
    var updateVersionUtil = new UpdateVersionUtil();
    var latestVersionRecord = updateVersionUtil.findLatestVersion(record);

	if(updateVersionUtil.isVersionFromUpdateSet(latestVersionRecord)){
		var versionData = updateVersionUtil.getVersionData(latestVersionRecord);
		versionData.remote_script_sys_id = remote_script_sys_id;

		//gs.info(JSON.stringify(versionData));

		var response = new HttpService().createNewChangeVersion(versionData);

		gs.info(JSON.stringify(response));

		return response;
	}

	return undefined;
  },

  type: "TracyService"
};
