var story=new GlideRecord("x_trz_update_sets");
story.addQuery("story",current.sys_id);
story.addQuery("test_move_status","Previewed");
story.addQuery("marked_for_deployment",true);
story.orderBy("order");
story.setLimit(1);
story.query();
if(story.next()){
		var remote_updateset = new GlideRecord('sys_remote_update_set');
		remote_updateset.addQuery('remote_sys_id', story.dev_sys_id);
		remote_updateset.query();
		remote_updateset.next();
		if(!remote_updateset.sys_id.nil()) {
			var lsysid= new global.TracyGlobalUtil2(remote_updateset.sys_id + '');
				 }
	}
else{
	current.deployment_state="deploy_test_success";
	}

