//script to start the progress worker, this will just trigger the method and then the next activity will wait till its complete
current.deployment_state="deploy_test_progress";
current.deployment_log="Retrieving update sets from Dev";
var gr = new GlideRecord('sys_update_set_source');
gr.addQuery('active', true);
gr.addQuery("name","Tracy - Update Source dev");
gr.addQuery('type','dev');
gr.query();
if(gr.next()){
		var progressId = new global.TracyGlobalUtil().startWorker(gr.sys_id + '');
}
else{
	current.deployment_log="Missing update source:Tracy - Update Source dev.Please make sure that there is an update source to Dev instance name exactly like this";
	current.deployment_state="deploy_test_fail";
}

