try{
var uri="https://"+gs.getProperty('x_trz.prod_instance')+".service-now.com/tracyStoryReceiver.do?SOAP";
var s = new sn_ws.SOAPMessageV2('tracyStoryReceiver', 'execute');
	s.setEndpoint(uri);
	s.setStringParameter('execute.sys_id', current.sys_id.toString());	
	s.setStringParameter('execute.deployment_state', current.deployment_state);
	s.setStringParameter('execute.deployment_log', "Cannot access update source: Tracy - Update Source dev");
	var response = s.execute();
} catch(ex) {
	gs.error(ex);
}

